mapper.select:
SELECT id, mcp_server_id, tool_name, description, enabled, created_by, updated_by, created_at, updated_at
FROM tool
WHERE mcp_server_id = #{mcpServerId}

SELECT id, mcp_server_id, tool_name, description, enabled, created_by, updated_by, created_at, updated_at
FROM tool
WHERE tool_name = #{toolName}
AND mcp_server_id = #{mcpServerId}
